import { useState } from "react";
export function Mainn() {
  let [validOrNot, setValidOrNot] = useState(["valid", "notValid", "notValid"]);
  const [data, setData] = useState([
    {
      planet: "MERCURY",
      color: "#419ebb",
      img: "https://d33wubrfki0l68.cloudfront.net/2412cfbc443beec064c6a662be23adfa4bb9cbb6/e883c/assets/planet-mercury.svg",
      pra: [
        "Mercury is the smallest planet in the Solar System and the closest to the Sun. Its orbit around the Sun takes 87.97 Earth days, the shortest of all the Sun's planets. Mercury is one of four terrestrial planets in the Solar System, and is a rocky body like Earth.",
        "Mercury appears to have a solid silicate crust and mantle overlying a solid, iron sulfide outer core layer, a deeper liquid core layer, and a solid inner core. The planet's density is the second highest in the Solar System at 5.427 g/cm3 , only slightly less than Earth's density.",
        "Mercury's surface is similar in appearance to that of the Moon, showing extensive mare-like plains and heavy cratering, indicating that it has been geologically inactive for billions of years. It is more heterogeneous than either Mars's or the Moon’s.",
      ],
      detail: {
        rotationTime: "58.6 DAYS",
        revolutionTime: "87.97 DAYS",
        radius: "2,439.7 KM",
        averageTemp: "430°C",
      },
    },
  ]);
  function changeValid(n) {
    let tempState = [...validOrNot];
    let tempElement = { ...tempState[n] };
    tempElement = "valid";
    tempState[n] = tempElement;
    setValidOrNot(["notValid", "notValid", "notValid"]);
    setValidOrNot(tempState);
  }
  return (
    <div className="mainn">
      <div className="d-flex align-items-center justify-content-center figure">
        <img
          src={data[0].img}
          alt=""
          style={{
            width: "200px",
          }}
        />
      </div>
      <div className="about">
        <div>
          <h1 className="mb-4" style={{ fontSize: "45px" }}>
            {data[0].planet}
          </h1>
          <p
            style={{
              color: "white",
              fontFamily: "Spartan, sans-serif",
              fontSize: "12px",
              lineHeight: "25px",
              marginBottom: "35px",
            }}
          >
            {data[0].pra}
          </p>
          <p
            style={{
              fontSize: "12px",
              fontFamily: "Spartan, sans-serif",
              color: "#eee",
              color: "#7A7A89",
              margin: 0,
            }}
          >
            Source :
            <a
              href="https://en.wikipedia.org/wiki/Mercury_%28planet%29"
              target="_blank"
            >
              <b
                style={{
                  fontFamily: "Spartan, sans-serif",
                  color: "#eee",
                  color: "#7A7A89",
                }}
              >
                Wikipedia
              </b>
              <img
                src="https://d33wubrfki0l68.cloudfront.net/7161506cc08ae44d2b3d0adead5ed19c0f4806a0/7c131/assets/icon-source.svg"
                alt="Source Icon"
                style={{
                  width: "10px",
                  height: "10px",
                  margin: 0,
                  marginLeft: "3px",
                  marginBottom: "5px",
                }}
              ></img>
            </a>
          </p>
        </div>
      </div>
      <div
        className="d-flex flex-column justify-content-end  choose h-100"
        style={{ gap: "17px" }}
      >
        <button
          className={validOrNot[0]}
          onClick={() => {
            changeValid(0);
          }}
        >
          <span>01</span> OVERVIEW
        </button>
        <button
          className={validOrNot[1]}
          onClick={() => {
            changeValid(1);
          }}
        >
          <span>02</span>STRUCTURE
        </button>
        <button
          className={validOrNot[2]}
          onClick={() => {
            changeValid(2);
          }}
        >
          <span>03</span>SURFACE
        </button>
      </div>
      <div>
        <div className="d-flex justify-content-between details">
          <div>
            <div>
              <p>ROTATION TIME</p>
              <h6>58.6 DAYS</h6>
            </div>
          </div>
          <div>
            <div>
              <p>REVOLUTION TIME</p>
              <h6>58.6 DAYS</h6>
            </div>
          </div>
          <div>
            <div>
              <p>RADIUS</p>
              <h6>58.6 DAYS</h6>
            </div>
          </div>
          <div>
            <div>
              <p>AVERAGE TEMP.</p>
              <h6>58.6 DAYS</h6>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
